(12, 'ط§ظ„ط¯ط±ط³ ط§ظ„ط®ط§ظ…ط³', 'ط§ظ„ط¯ط±ط³ ط§ظ„ط®ط§ظ…ط³ ', '&lt;p&gt;&amp;lt;?php&lt;/p&gt;\r\n&lt;p&gt;if(!isset($_SESSION)){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; session_start();&lt;/p&gt;\r\n&lt;p&gt;}&lt;/p&gt;\r\n&lt;p&gt;include(&#039;../dbConnection.php&#039;);&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;// Check authentication&lt;/p&gt;\r\n&lt;p&gt;if(!isset($_SESSION[&#039;is_login&#039;])){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; echo &quot;&amp;lt;script&amp;gt; location.href=&#039;../index.php&#039; &amp;lt;/script&amp;gt;&quot;;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; exit();&lt;/p&gt;\r\n&lt;p&gt;}&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;// Get current lesson details&lt;/p&gt;\r\n&lt;p&gt;$current_lesson = [];&lt;/p&gt;\r\n&lt;p&gt;if(isset($_GET[&#039;lesson_id&#039;])){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; $lesson_id = $_GET[&#039;lesson_id&#039;];&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; $sql = &quot;SELECT * FROM lesson WHERE lesson_id = &#039;$lesson_id&#039;&quot;;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; $result = $conn-&amp;gt;query($sql);&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; if($result-&amp;gt;num_rows == 1){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $current_lesson = $result-&amp;gt;fetch_assoc();&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; // Convert attachments JSON to array&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if(!empty($current_lesson[&#039;attachments&#039;])){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $current_lesson[&#039;attachments&#039;] = json_decode($current_lesson[&#039;attachments&#039;], true);&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; }&lt;/p&gt;\r\n&lt;p&gt;}&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;// Get course details&lt;/p&gt;\r\n&lt;p&gt;$course_id = isset($_GET[&#039;course_id&#039;]) ? $_GET[&#039;course_id&#039;] : (isset($current_lesson[&#039;course_id&#039;]) ? $current_lesson[&#039;course_id&#039;] : null);&lt;/p&gt;\r\n&lt;p&gt;$course = [];&lt;/p&gt;\r\n&lt;p&gt;if($course_id){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; $sql = &quot;SELECT * FROM course WHERE course_id = &#039;$course_id&#039;&quot;;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; $result = $conn-&amp;gt;query($sql);&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; if($result-&amp;gt;num_rows == 1){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $course = $result-&amp;gt;fetch_assoc();&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; }&lt;/p&gt;\r\n&lt;p&gt;}&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;// Get all lessons for this course&lt;/p&gt;\r\n&lt;p&gt;$lessons = [];&lt;/p&gt;\r\n&lt;p&gt;if($course_id){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; $sql = &quot;SELECT * FROM lesson WHERE course_id = &#039;$course_id&#039; ORDER BY lesson_order ASC&quot;;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; $result = $conn-&amp;gt;query($sql);&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; if($result-&amp;gt;num_rows &amp;gt; 0){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; while($row = $result-&amp;gt;fetch_assoc()){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $lessons[] = $row;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; }&lt;/p&gt;\r\n&lt;p&gt;}&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;// Find current lesson index for navigation&lt;/p&gt;\r\n&lt;p&gt;$current_lesson_index = null;&lt;/p&gt;\r\n&lt;p&gt;if(!empty($current_lesson) &amp;amp;&amp;amp; !empty($lessons)){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; foreach($lessons as $index =&amp;gt; $lesson){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if($lesson[&#039;lesson_id&#039;] == $current_lesson[&#039;lesson_id&#039;]){&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $current_lesson_index = $index;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; break;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; }&lt;/p&gt;\r\n&lt;p&gt;}&lt;/p&gt;\r\n&lt;p&gt;?&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;lt;!DOCTYPE html&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;lt;html lang=&quot;en&quot;&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;lt;head&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;meta charset=&quot;UTF-8&quot;&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;meta name=&quot;viewport&quot; content=&quot;width=device-width, initial-scale=1.0&quot;&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;title&amp;gt;&amp;lt;?php echo isset($current_lesson[&#039;lesson_name&#039;]) ? htmlspecialchars($current_lesson[&#039;lesson_name&#039;]) : &#039;Lesson&#039;; ?&amp;gt; | CodeKids&amp;lt;/title&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;!-- Favicon --&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;link rel=&quot;shortcut icon&quot; type=&quot;image/png&quot; href=&quot;../images/logo.png&quot;&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;!-- Font Awesome --&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;link rel=&quot;stylesheet&quot; href=&quot;https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css&quot;&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;!-- Google Fonts --&amp;gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;lt;link href=&quot;https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;7